import React, {useEffect, useState} from 'react';
import api from '../../services/api.js';
import $ from "jquery";

export default function Dashboard({history}) {
    
    const[title, setTitle] = useState('');
    const[text, setText] = useState('');
    const[type, setType] = useState('normal');

    async function handleSubmit(event){
        event.preventDefault();
        console.log(title);
        console.log(text);
        console.log(type);

        // try {
        //     const response = await api.post("/sessions", { login, password });
        //     //console.log(response);
            
        //     history.push('/dashboard');
        // }
        // catch(e){
        //     //console.log(e);
        //     $(".error").html("<div>Dados de login inválidos!</div>");
        // }

    }

    return (
        <>
        <div className="container">
            <div className="box_alerts">
                <div className="title">Novo Alerta</div>
                <form onSubmit={handleSubmit}>
                    <input 
                        required="required"
                        type="text" 
                        placeholder="Título" 
                        className="title2"
                        value={title}
                        onChange={event => setTitle(event.target.value)}
                    />
                    <textarea 
                        placeholder="Texto" 
                        className="text"
                        value={text}
                        onChange={event => setText(event.target.value)}
                    />
                    <select value={type} onChange={event => setType(event.target.value)}>
                        <option value="normal">Cinza</option>
                        <option value="green">Verde</option>
                        <option value="red">Vermelho</option>
                    </select>
                    
                    <button className="button" type="submit">ENVIAR ALERTA</button>
                </form>
            </div>
        </div>
        </>
    );
}
