import React from 'react';
import api from './services/api.js';
import { useInterval } from './polling.js';
import './App.css';

function App() {

  getAlert();
  useInterval(() => {
    getAlert();
  }, 6000);

  function getAlert(){
    api.get('/alert')
    .then(function (response) {
      // handle success
      console.log(response);
    });


  }

  return (
    <>
      <div className="App">
        Esperando um novo alerta!
      </div>
      <div className="modal">
        <div className="box green">
          <div className="header"></div>
          <div className="title">Lala</div>
          <div className="text">alalalol</div>
          <div className="button">OK</div>
        </div>
      </div>
    </>
  );
}

export default App;
