import React, {useEffect, useState} from 'react';
import { isAuthenticated, getToken } from "../../services/auth";
import api from '../../services/api.js';
import $ from "jquery";

export default function Dashboard({history}) {

    useEffect(() => {
        if(!isAuthenticated()){
            history.push('/login');
        }
    }, []);

    const [alerts, setAlerts] = useState([]);
    const [userLogged, setUserLogged] = useState([]);

    useEffect(() => {
        async function loadAlerts(){
            const response = await api.get('/allAlerts');
            //console.log(response);
            setAlerts(response.data.alerts);
        }

        loadAlerts();

        async function loadUserLogged(){
            alert(getToken());
            const response = await api.get('/user', { _id: getToken() });
            console.log(response);
            setUserLogged(response.data);
        }

        loadUserLogged();
    }, []);


    return (
        <>
        <div className="container">
            
            <div className="box_alerts">
                <div className="title">Alertas <span>(total de {alerts.length} alertas)</span></div>
                <table>
                    <thead>
                        <tr>
                            <th>Títutlo</th>
                            <th>Conteúdo</th>
                            <th>Data</th>
                            <th>Autor</th>
                        </tr>
                    </thead>
                    <tbody>
                        {alerts.map(alert => (
                            <tr key={alert._id}>
                                <td>{alert.title}</td>
                                <td>{alert.text}</td>
                                <td>{alert.date_exhibition}</td>
                                <td>{alert.user}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
        </>
    );
}
